import React, { useState } from 'react';
import { View, Text, StyleSheet, TouchableOpacity } from 'react-native';
import { Ionicons } from '@expo/vector-icons';

const NewProfile = () => {
  const [selectedType, setSelectedType] = useState(null);

  const handleTypeSelection = (type) => {
    setSelectedType(type);
  };

  const handleContinue = () => {
    // Update the user's "type" attribute in Firestore with the selectedType
    // Perform any necessary actions upon pressing continue
  };

  return (
    <View style={styles.container}>
      <View style={styles.iconContainer}>
        <TouchableOpacity
          style={[
            styles.icon,
            selectedType === 'brand' && styles.selectedIcon,
          ]}
          onPress={() => handleTypeSelection('brand')}
        >
          <Ionicons name="business-outline" size={48} color="#E39727" />
        </TouchableOpacity>
        <TouchableOpacity
          style={[
            styles.icon,
            selectedType === 'influencer' && styles.selectedIcon,
          ]}
          onPress={() => handleTypeSelection('influencer')}
        >
          <Ionicons name="person-outline" size={48} color="#E39727" />
        </TouchableOpacity>
      </View>
      <TouchableOpacity
        style={styles.continueButton}
        onPress={handleContinue}
        disabled={!selectedType}
      >
        <Text style={styles.buttonText}>Continue</Text>
      </TouchableOpacity>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
  },
  iconContainer: {
    flexDirection: 'row',
    justifyContent: 'space-around',
    marginBottom: 40,
  },
  icon: {
    borderWidth: 1,
    borderColor: '#E39727',
    borderRadius: 50,
    padding: 16,
  },
  selectedIcon: {
    backgroundColor: '#E39727',
  },
  continueButton: {
    backgroundColor: '#E39727',
    paddingVertical: 12,
    paddingHorizontal: 24,
    borderRadius: 4,
  },
  buttonText: {
    color: '#fff',
    fontSize: 18,
    fontWeight: 'bold',
  },
});

export default NewProfile;
