import { useNavigation } from '@react-navigation/native'
import React, { useEffect, useLayoutEffect, useRef } from 'react'
import {View, Text, Button, Image, StyleSheet, ImageBackground, Dimensions} from 'react-native'
import { NavigationProp } from '@react-navigation/native'
import { FIREBASE_AUTH } from '../firebase'
import { SafeAreaView } from 'react-native-safe-area-context'
import { TouchableOpacity } from 'react-native-web'
import { getAuth } from 'firebase/auth'
import Images from '../images/Images'
import { AntDesign, Entypo, Ionicons, MaterialIcons } from "@expo/vector-icons";
import Swiper from 'react-native-deck-swiper';

const DUMMY_DATA = [
    {
        brandName: "Tesla",
        productName: "Model S Plaid",
        category: "Car",
        photoURL: "https://ev-database.org/img/auto/Tesla_Model_S_2021/Tesla_Model_S_2021-01.jpg",
        id: 123,
    },
    {
        brandName: "Tesla",
        productName: "Model S Plaid",
        category: "Car",
        photoURL: "https://ev-database.org/img/auto/Tesla_Model_S_2021/Tesla_Model_S_2021-01.jpg",
        id: 456,
    },
    {
        brandName: "Tesla",
        productName: "Model S Plaid",
        category: "Car",
        photoURL: "https://ev-database.org/img/auto/Tesla_Model_S_2021/Tesla_Model_S_2021-01.jpg",
        id: 789,
    }
];

const screenHeight = Dimensions.get('window').height;
const cardHeight = screenHeight * 0.75;

const HomeScreen = () => {
    const navigation = useNavigation();
    const auth = getAuth();
    const user = auth.currentUser;
    const staticImage = require("../images/Logo.png");
    const profile = require("../images/profileIcon.png");
    const chatIcon = require("../images/chat.png");  
    const swipeRef = useRef(null);
    
    //console.log('This is home screen user: ', user);

    useEffect(() => {
        navigation.setOptions({
           headerShown: false,
        });
      }, [navigation]);

    return(
        <View>
            <View style={styles.container}>
                <TouchableOpacity onPress={() => FIREBASE_AUTH.signOut()}>
                    <MaterialIcons name="account-circle" style={styles.imageLeft} size={50} color="#eba134" />
                </TouchableOpacity>
                <TouchableOpacity>
                    <Image source={staticImage} style={styles.imageCenter} alt="logo"/>
                </TouchableOpacity>
                <TouchableOpacity onPress={() => navigation.navigate("Chat")}>
                    <Ionicons name="chatbubbles-sharp" size={50} style={styles.imageRight} color="#eba134"/>
                </TouchableOpacity>             
            </View>
            <View>
                <Swiper 
                    ref={swipeRef}
                    containerStyle={{backgroundColor: "transparent"}}
                    cards={DUMMY_DATA}
                    stackSize={5}
                    cardIndex={0}
                    animateCardOpacity
                    verticalSwipe={false}
                    onSwipedLeft={() => {
                        console.log('Swipe Pass')
                    }}
                    onSwipedRight={() => {
                        console.log('Swipe match')
                    }}
                    overlayLabels={{
                        left: {
                            title: "NOPE",
                            style: {
                                label: {
                                    textAlign: "right",
                                    color: "red",
                                },
                            },
                        },
                        right: {
                            title: "Match",
                            style: {
                                label: {
                                    textAlign: "left",
                                    color: "green",
                                },
                            },
                        }
                    }}
                    renderCard={(card) => (
                        <View key={card.id} style={[styles.card, styles.cardShadow]}>
                            <Image style={styles.cardImg} source={{uri: card.photoURL }}/>
                            <View style={styles.cardTxt}>
                                <View>
                                    <Text style={styles.brandName}>{card.brandName}</Text>
                                    <Text style={styles.productName}>{card.productName}</Text>
                                </View>
                                <Text style={styles.category}>{card.category}</Text>
                            </View>
                        </View>
                        
                    )}
                />
            </View>
            <View style={styles.swipeButtons}>
                <TouchableOpacity style={styles.crossButton}>
                    <Entypo name="cross" size={24} color="#fff"/>
                </TouchableOpacity>
                <TouchableOpacity style={styles.heartButton}>
                    <AntDesign name="heart" size={24} color="#fff"/>
                </TouchableOpacity>
            </View>
        </View>
        )
}

export default HomeScreen


const styles = StyleSheet.create({
    deck: {
        flex: 1,
    },
    container: {
        flexDirection: 'row',
        justifyContent: 'space-between',
        alignItems: 'center',
        paddingTop: 10,
    },
    imageLeft: {
        marginLeft: 10,
    },
    imageCenter: {
        width: 50,
        height: 50,
    },
    imageRight: {
        width: 65,
        height: 50,
        marginRight: 10,
    },
    text: {
        fontSize: 50,
        margin: 50,
        justifyContent: 'center',
    },
    card: {
        position: 'relative',
        backgroundColor: 'white',
        borderRadius: 10,
        padding: 16,
        height: cardHeight,
    },
    cardImg: {
        position: 'absolute',
        borderRadius: 10,
        top: 0,
        left: 0,
        width: '100%',
        height: '100%',
    },
    cardTxt: {
        position: 'absolute',
        borderRadius: 10,
        bottom: 0,
        left: 0,
        width: '100%',
        backgroundColor: 'white',
        padding: 16,
        flexDirection: 'row',
        justifyContent: 'space-between',
        alignItems: 'center',
      },
      brandName: {
        fontWeight: 'bold',
        fontSize: 18,
      },
      productName: {
        fontSize: 16,
      },
      category: {
        fontStyle: 'italic',
      },
      cardShadow: {
        shadowColor: "#000",
        shadowOffset: {
            width: 0,
            height: 1,
        },
        shadowOpacity: 0.2,
        shadowRadius: 1.41,
        elevation: 2,

      },
      swipeButtons: {
        flexDirection: 'row',
        justifyContent: 'space-evenly',
        backgroundColor: '#fff',
        borderRadius: 10,
        paddingVertical: 8,
        paddingHorizontal: 16,
      },
      crossButton: {
        backgroundColor: 'red',
        borderRadius: 20,
        padding: 8,
      },
      heartButton: {
        backgroundColor: 'green',
        borderRadius: 20,
        padding: 8,
      },

});
