import React from 'react'
import {View, Text, TextInput, Button, KeyboardAvoidingView, Platform} from 'react-native'
import Header from '../components/Header';
import getMatchedUserInfo from '../lib/getMatchedUserInfo';
import { getAuth } from 'firebase/auth';
import { useRoute } from '@react-navigation/native';

const MessageScreen = () => {
    const auth = getAuth();
    const user = auth.currentUser;
    const {params} =  useRoute();
    const {matchDetails} = params;
    const [input, setInput] = useState('');

    const sendMessage = () => {

    }

    return (
        <View style={{flex: 1}}>
            <Header title={getMatchedUserInfo(matchDetails?.users, user.uid).displayName}  callEnabled/>
            <KeyboardAvoidingView
                behavior={Platform.OS === "ios" ? "padding" : "height"}
                style={{flex: 1}}
                keyboardVerticalOffset={10}
            >
                <View>
                    <TextInput
                        style={{height: 10, text: large}}
                        placeholder='Send Message...'
                        onChangeText={setInput}
                        onSubmitEditing={sendMessage}
                        value={input}
                    />
                    <Button onPress={sendMessage} title="Send" color="#eba134"/>
                </View>
            </KeyboardAvoidingView>
            
        </View>
    )
}

export default MessageScreen;
