import React, { useState } from 'react';
import { View, Text, TextInput, TouchableOpacity, StyleSheet, Image } from 'react-native';
import { Ionicons } from '@expo/vector-icons';
import { useNavigation } from '@react-navigation/native';
import { getAuth } from 'firebase/auth';

const ModalScreen = () => {
  const navigation = useNavigation();
  const [profilePicture, setProfilePicture] = useState('');
  const [category, setCategory] = useState('');
  const [age, setAge] = useState('');
  const staticImage = require("../images/Logo.png");
  const auth = getAuth();
  const user = auth.currentUser;

  const handleUpdateProfile = () => {
    // Logic for updating the profile
    // You can access the values of profilePicture, category, and age here
    console.log('Profile updated!');
  };

  const styles = StyleSheet.create({
    container: {
      flex: 1,
      justifyContent: 'center',
      alignItems: 'center',
      paddingHorizontal: 16,
    },
    header: {
      flexDirection: 'row',
      alignItems: 'center',
      marginBottom: 16,
    },
    backButton: {
      marginRight: 8,
    },
    logoText: {
      fontSize: 24,
      fontWeight: 'bold',
    },
    inputContainer: {
      marginBottom: 16,
      width: '100%',
    },
    input: {
      borderWidth: 1,
      borderColor: '#ccc',
      borderRadius: 4,
      padding: 8,
      marginBottom: 8,
    },
    button: {
      backgroundColor: '#007bff',
      borderRadius: 4,
      paddingVertical: 12,
      paddingHorizontal: 24,
    },
    buttonText: {
      color: '#fff',
      fontSize: 16,
      fontWeight: 'bold',
      textAlign: 'center',
    },
    logoImg: {
        height: 50,
        width: 50,
    }
  });

  return (
    <View style={styles.container}>
      <View style={styles.header}>
        <TouchableOpacity style={styles.backButton} onPress={() => navigation.goBack()}>
          <Ionicons name="arrow-back-circle-outline" size={24} color="black" />
        </TouchableOpacity>
        <Image source={staticImage} style={styles.logoImg} alt="logo"/>
        <Text style={styles.logoText}>InfluGuru</Text>
      </View>
      <View style={styles.inputContainer}>
        <Text>Welcome {user.displayName}</Text>
        <TextInput
          style={styles.input}
          placeholder="Enter Profile Picture URL"
          value={profilePicture}
          onChangeText={setProfilePicture}
        />
        <TextInput
          style={styles.input}
          placeholder="Enter Category"
          value={category}
          onChangeText={setCategory}
        />
        <TextInput
          style={styles.input}
          placeholder="Enter Age"
          keyboardType="numeric"
          value={age}
          onChangeText={setAge}
        />
      </View>
      <TouchableOpacity style={styles.button} onPress={handleUpdateProfile}>
        <Text style={styles.buttonText}>Update Profile</Text>
      </TouchableOpacity>
    </View>
  );
};

export default ModalScreen;
