import { useNavigation } from '@react-navigation/native'
import React, { useEffect, useLayoutEffect } from 'react'
import {View, Text, Button, Image, StyleSheet, ImageBackground} from 'react-native'
import { NavigationProp } from '@react-navigation/native'
import { FIREBASE_AUTH } from '../firebase'
import { SafeAreaView } from 'react-native-safe-area-context'
import { TouchableOpacity } from 'react-native-web'
import { getAuth } from 'firebase/auth'
import Images from '../images/Images'

const staticImage = require("../images/Logo.png");
const HomeScreen = () => {
    const navigation = useNavigation();
    const auth = getAuth();
    const user = auth.currentUser;
    
    //console.log('This is home screen user: ', user);

    useEffect(() => {
        navigation.setOptions({
           headerShown: false,
        });
      }, [navigation]);

    return(
        <SafeAreaView>
            <View style={styles.container}>
                
                <ImageBackground 
                    className="h-10 w-10 rounded-full" 
                    source={{uri:user.photoURL}} 
                    styles={styles.img}
                    alt="ProfilePic">
                </ImageBackground>
                
                <ImageBackground  source={staticImage} styles={styles.img} alt="logo">
                    </ImageBackground>
                
                            
            </View>

            <View style={styles.text}>
                <Text>home screen.</Text>
                <Button title="Chat" onPress={() => navigation.navigate("Chat")}/>
                <Button title="Logout" onPress={() => FIREBASE_AUTH.signOut()}/>
            </View>
        </SafeAreaView>
        )
}

export default HomeScreen


const styles = StyleSheet.create({
    container: {
        flex: 1,
        justifyContent: 'center',
    },
    img: {
        marginLeft: 900,
        flex: 1,
        resizeMode: 'cover',
    },
    text: {
        fontSize: 50,
        margin: 50,
        justifyContent: 'center',
    }

});
