import { useNavigation } from '@react-navigation/native'
import React, { useEffect, useLayoutEffect } from 'react'
import {View, Text, Button, Image, StyleSheet, ImageBackground} from 'react-native'
import { NavigationProp } from '@react-navigation/native'
import { FIREBASE_AUTH } from '../firebase'
import { SafeAreaView } from 'react-native-safe-area-context'
import { TouchableOpacity } from 'react-native-web'
import { getAuth } from 'firebase/auth'
import Images from '../images/Images'
import { AntDesign, Entypo, Ionicons, MaterialIcons } from "@expo/vector-icons";


const HomeScreen = () => {
    const navigation = useNavigation();
    const auth = getAuth();
    const user = auth.currentUser;
    const staticImage = require("../images/Logo.png");
    const profile = require("../images/profileIcon.png");
    const chatIcon = require("../images/chat.png");    
    //console.log('This is home screen user: ', user);

    useEffect(() => {
        navigation.setOptions({
           headerShown: false,
        });
      }, [navigation]);

    return(
        <View>
            <View style={styles.container}>
                <TouchableOpacity onPress={() => FIREBASE_AUTH.signOut()}>
                    <MaterialIcons name="account-circle" style={styles.imageLeft} size={50} color="#eba134" />
                </TouchableOpacity>
                <TouchableOpacity>
                    <Image source={staticImage} style={styles.imageCenter} alt="logo"/>
                </TouchableOpacity>
                <TouchableOpacity onPress={() => navigation.navigate("Chat")}>
                    <Ionicons name="chatbubbles-sharp" size={50} style={styles.imageRight} color="#eba134"/>
                    {/*<Image source={chatIcon} alt="logo"/>*/}
                </TouchableOpacity>             
            </View>
        </View>
        )
}

export default HomeScreen


const styles = StyleSheet.create({
    container: {
        flexDirection: 'row',
        justifyContent: 'space-between',
        alignItems: 'center',
        paddingTop: 10,
    },
    imageLeft: {
        marginLeft: 10,
    },
    imageCenter: {
        width: 50,
        height: 50,
    },
    imageRight: {
        width: 65,
        height: 50,
        marginRight: 10,
    },
    text: {
        fontSize: 50,
        margin: 50,
        justifyContent: 'center',
    }

});
