import React, { useEffect, useState } from 'react';
import { View, TextInput, StyleSheet, Button, Text, ImageBackground, Dimensions, Image } from 'react-native';
import { ActivityIndicator } from 'react-native';
import { createUserWithEmailAndPassword, signInWithEmailAndPassword } from 'firebase/auth';
import { useNavigation } from '@react-navigation/native';
import useAuth from '../hooks/useAuth';
import { FIREBASE_AUTH } from '../firebase';

const staticImage = require('../images/Logo.png');

const LoginScreen = () => {
  const navigation = useNavigation();
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [loading, setLoading] = useState(false);
  const auth = FIREBASE_AUTH;

  useEffect(() => {
    navigation.setOptions({
      headerShown: false,
    });
  }, [navigation]);

  const signIn = async () => {
    setLoading(true);
    try {
      const response = await signInWithEmailAndPassword(auth, email, password);
      console.log(response);
    } catch (error) {
      console.log(error);
    } finally {
      setLoading(false);
    }
  };

  const signUp = async () => {
    setLoading(true);
    try {
      const response = await createUserWithEmailAndPassword(auth, email, password);
      console.log(response);
      alert('Check your email!');
    } catch (error) {
      console.log(error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <View style={styles.container}>
      <View style={styles.background}>
        <View style={styles.logoContainer}>
          <Image source={staticImage} />
        </View>
        <View style={styles.bottomContainer}>
          <Text style={styles.infoText}>Login/Sign up to continue</Text>
          <View style={styles.inputContainer}>
            <TextInput
              value={email}
              style={styles.input}
              placeholder="Email"
              autoCapitalize="none"
              onChangeText={(text) => setEmail(text)}
              placeholderTextColor="#E39727"
            />
            <TextInput
              value={password}
              secureTextEntry={true}
              style={styles.input}
              placeholder="Password"
              autoCapitalize="none"
              onChangeText={(text) => setPassword(text)}
              placeholderTextColor="#E39727"
            />
          </View>
          {loading ? (
            <ActivityIndicator size="large" color="#0000ff" />
          ) : (
            <>
              <Button title="Login" onPress={signIn} color="#E39727" />
              <Button title="Create an Account" onPress={signUp} color="#E39727" />
            </>
          )}
        </View>
      </View>
    </View>
  );
};

export default LoginScreen;

const windowWidth = Dimensions.get('window').width;
const windowHeight = Dimensions.get('window').height;

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    backgroundColor: '#fff',
  },
  background: {
    flex: 1,
    resizeMode: 'cover',
  },
  logoContainer: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
  },
  logoText: {
    fontSize: 24,
    fontWeight: 'bold',
  },
  bottomContainer: {
    flex: 2,
    paddingHorizontal: 32,
    paddingBottom: windowHeight * 0.05,
  },
  infoText: {
    fontSize: 18,
    fontWeight: '600',
    marginBottom: 24,
    fontFamily: 'Rubik',
    fontWeight: 'bold',
  },
  inputContainer: {
    marginBottom: 24,
  },
  input: {
    marginVertical: 8,
    height: 50,
    borderWidth: 1,
    borderRadius: 4,
    padding: 10,
    backgroundColor: '#fff',
    borderColor: '#E39727',
    color: '#000',
    fontFamily: 'Rubik',
    fontWeight: 'bold',
  },
});
