import React, { useState } from 'react';
import { SafeAreaView, View, Text, TouchableOpacity, StyleSheet } from 'react-native';
import Header from '../components/Header';

const InterestsScreen = () => {
  const [selectedInterests, setSelectedInterests] = useState([]);

  const handleToggleInterest = (interest) => {
    if (selectedInterests.includes(interest)) {
      setSelectedInterests(selectedInterests.filter((item) => item !== interest));
    } else {
      setSelectedInterests([...selectedInterests, interest]);
    }
  };

  const interests = [
    { id: 1, label: 'Clothing' },
    { id: 2, label: 'Fitness' },
    { id: 3, label: 'Nutrition' },
    { id: 4, label: 'Technology' },
    { id: 5, label: 'Gaming' },
    { id: 6, label: 'Food' },
    { id: 7, label: 'Adventure' },
    { id: 8, label: 'Music' },
    { id: 9, label: 'Dance' },
    { id: 10, label: 'Yoga' },
    { id: 11, label: 'Business' },
    { id: 12, label: 'Others' },
  ];

  const renderInterestButton = (interest) => {
    const isSelected = selectedInterests.includes(interest.label);
    const buttonStyles = [styles.interestButton, isSelected && styles.selectedInterestButton];
    const textStyles = [styles.interestButtonText, isSelected && styles.selectedInterestButtonText];

    return (
      <TouchableOpacity
        key={interest.id}
        style={buttonStyles}
        onPress={() => handleToggleInterest(interest.label)}
      >
        <Text style={textStyles}>{interest.label}</Text>
      </TouchableOpacity>
    );
  };

  return (
    <SafeAreaView style={styles.container}>
      <Header title="Your Interests" />
      <View style={styles.textContainer}>
        <Text style={styles.descriptionText}>
          Select a few of your interests for brand collaborations. Please keep in mind your social
          media content while picking your interests.
        </Text>
      </View>
      <View style={styles.buttonsContainer}>
        {interests.map((interest) => renderInterestButton(interest))}
      </View>
      <View style={styles.confirmButtonContainer}>
        <TouchableOpacity style={styles.confirmButton}>
            <Text style={styles.confirmButtonText}>Confirm</Text>
        </TouchableOpacity>
      </View>
    </SafeAreaView>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    paddingHorizontal: 16,
    paddingTop: 16,
  },
  textContainer: {
    marginVertical: 16,
  },
  descriptionText: {
    fontSize: 20,
    color: 'grey',
    fontFamily: 'Rubik',
    textAlign: 'center',
  },
  interestButton: {
    width: '40%', // Adjust the width to occupy 40% of the container (2 columns)
    height: 60,
    margin: 8,
    borderRadius: 30,
    borderWidth: 1,
    borderColor: '#E39727',
    justifyContent: 'center',
    alignItems: 'center',
  },
  selectedInterestButton: {
    backgroundColor: '#E39727',
  },
  interestButtonText: {
    color: '#E39727',
  },
  selectedInterestButtonText: {
    color: '#fff',
  },
  buttonsContainer: {
    flex: 1,
    flexDirection: 'row',
    flexWrap: 'wrap',
    justifyContent: 'center',
    alignItems: 'center',
    marginHorizontal: 16,
    marginTop: 16,
    marginBottom: 60, // Adjust the margin bottom to create space for the confirm button
  },
  confirmButtonContainer: {
    position: 'absolute',
    bottom: 0,
    left: 0,
    right: 0,
    justifyContent: 'center',
    alignItems: 'center',
  },
  confirmButton: {
    width: '40%', // Adjust the width to your preference
    height: 60,
    marginVertical: 16,
    borderRadius: 30,
    backgroundColor: '#E39727',
    justifyContent: 'center',
    alignItems: 'center',
  },
  confirmButtonText: {
    color: '#fff',
    fontSize: 16,
    fontWeight: 'bold',
  },
});

export default InterestsScreen;
