import React, { useState } from 'react';
import { View, Text, TextInput, TouchableOpacity, StyleSheet, Image, DatePickerAndroid, Picker } from 'react-native';
import { Ionicons } from '@expo/vector-icons';
import { useNavigation } from '@react-navigation/native';

const ModalScreen = () => {
  const navigation = useNavigation();
  const [image, setImage] = useState('');
  const [firstName, setFirstName] = useState('');
  const [lastName, setLastName] = useState('');
  const [dateOfBirth, setDateOfBirth] = useState(new Date());
  const [gender, setGender] = useState('Male');
  const staticImage = require("../images/Logo.png");

  const incompleteForm = !image || !firstName || !lastName || !gender;

  const handleUpdateProfile = () => {
    // Logic for updating the profile
    // You can access the values of firstName, lastName, dateOfBirth, and gender here
    console.log('Profile updated!');
  };

  /*const openDatePicker = async () => {
    try {
      const { action, year, month, day } = await DatePickerAndroid.open({
        date: dateOfBirth,
      });

      if (action !== DatePickerAndroid.dismissedAction) {
        const selectedDate = new Date(year, month, day);
        setDateOfBirth(selectedDate);
      }
    } catch (error) {
      console.warn('DatePickerAndroid error:', error);
    }
  };*/

  const styles = StyleSheet.create({
    container: {
      flex: 1,
      paddingHorizontal: 16,
      paddingTop: 16,
    },
    header: {
      flexDirection: 'row',
      alignItems: 'center',
      justifyContent: 'space-between',
      marginBottom: 16,
    },
    backButton: {
      marginRight: 8,
    },
    logoImage: {
      width: 100,
      height: 100,
      resizeMode: 'contain',
    },
    heading: {
      fontSize: 24,
      fontWeight: 'bold',
      marginBottom: 16,
    },
    inputContainer: {
      marginBottom: 16,
    },
    input: {
      borderWidth: 1,
      borderColor: '#eba134',
      borderRadius: 4,
      padding: 8,
      marginBottom: 8,
    },
    datePickerButton: {
      borderWidth: 1,
      borderColor: '#eba134',
      borderRadius: 4,
      padding: 8,
      marginBottom: 8,
      justifyContent: 'center',
      alignItems: 'center',
    },
    datePickerText: {
      color: '#eba134',
    },
    picker: {
      borderWidth: 1,
      borderColor: '#eba134',
      borderRadius: 4,
      marginBottom: 8,
    },
    button: {
      borderRadius: 4,
      paddingVertical: 12,
      paddingHorizontal: 24,
      marginBottom: 16,
    },
    disabledButton: {
      backgroundColor: '#556073',
    },
    enabledButton: {
      backgroundColor: '#eba134',
    },
    buttonText: {
      color: '#fff',
      fontSize: 16,
      fontWeight: 'bold',
      textAlign: 'center',
    },
  });

  return (
    <View style={styles.container}>
      <View style={styles.header}>
        <TouchableOpacity style={styles.backButton} onPress={() => navigation.goBack()}>
          <Ionicons name="arrow-back-circle-outline" size={24} color="black" />
        </TouchableOpacity>
        <Image source={staticImage} style={styles.logoImage} />
      </View>
      <Text style={styles.heading}>Profile Details</Text>
      <View style={styles.inputContainer}>
        <TextInput
          style={styles.input}
          placeholder="URL of your profile pic"
          value={image}
          onChangeText={setImage}
        />
        <TextInput
          style={styles.input}
          placeholder="First Name"
          value={firstName}
          onChangeText={setFirstName}
        />
        <TextInput
          style={styles.input}
          placeholder="Last Name"
          value={lastName}
          onChangeText={setLastName}
          />
          {/*<TouchableOpacity style={styles.datePickerButton} onPress={openDatePicker}>
          <Text style={styles.datePickerText}>{dateOfBirth.toDateString()}</Text>
  </TouchableOpacity>*/}
          <Picker
          style={styles.picker}
          selectedValue={gender}
          onValueChange={(itemValue) => setGender(itemValue)}
          >
          <Picker.Item label="Male" value="Male" />
          <Picker.Item label="Female" value="Female" />
          <Picker.Item label="Others" value="Others" />
          </Picker>
          </View>
          <TouchableOpacity disabled={incompleteForm} style={[styles.button, incompleteForm ? styles.disabledButton : styles.enabledButton]} onPress={handleUpdateProfile}>
          <Text style={styles.buttonText}>Update Profile</Text>
          </TouchableOpacity>
          </View>
          );
          };
          
          export default ModalScreen;
